/// Core constants used throughout the SDK.\nlibrary;\n\n/// SDK version information.\nclass SDKVersion {\n  /// Current SDK version.\n  static const String version = '1.2.0';\n  \n  /// SDK name.\n  static const String name = 'All Fintech Flutter SDK';\n  \n  /// User agent string for HTTP requests.\n  static const String userAgent = '$name/$version';\n}\n\n/// HTTP configuration constants.\nclass HttpConstants {\n  /// Default request timeout in seconds.\n  static const int defaultTimeoutSeconds = 30;\n  \n  /// Maximum retry attempts for failed requests.\n  static const int maxRetryAttempts = 3;\n  \n  /// Retry delay in milliseconds.\n  static const int retryDelayMs = 1000;\n}\n\n/// Security constants.\nclass SecurityConstants {\n  /// Minimum API key length.\n  static const int minApiKeyLength = 10;\n  \n  /// Webhook signature header names.\n  static const Map<String, String> webhookHeaders = {\n    'paystack': 'x-paystack-signature',\n    'flutterwave': 'verif-hash',\n    'monnify': 'monnify-signature',\n  };\n}\n\n/// UI constants.\nclass UIConstants {\n  /// Default border radius for components.\n  static const double defaultBorderRadius = 8.0;\n  \n  /// Default padding for components.\n  static const double defaultPadding = 16.0;\n  \n  /// Animation duration in milliseconds.\n  static const int animationDurationMs = 300;\n}